// Navbar sticky
var navbar_sticky_on_top = 35;
function navbar_sticky_data_top() {
    var elem = jQuery('#main-menu-home');
    var body = jQuery('body');

    if (body.hasClass('navbar-fixed')) return;
    var offset = elem.offset();
    elem.attr('data-top', offset.top - navbar_sticky_on_top);
}
function navbar_sticky() {
    var elem = jQuery('#main-menu-home');
    var body = jQuery('body');

    if (!elem.attr('data-top')) navbar_sticky_data_top();
    if (elem.attr('data-top') <= jQuery(this).scrollTop()) {
        if (!body.hasClass('navbar-fixed')) {
            body.addClass('navbar-fixed');
            var scrollHeight = jQuery(this).scrollTop() + $('#navFixed').height();
            jQuery(this).scrollTop(scrollHeight);
        }
    } else {

        var isMobile = {
            Android: function () { return navigator.userAgent.match(/Android/i); },
            BlackBerry: function () { return navigator.userAgent.match(/BlackBerry/i); },
            iOS: function () { return navigator.userAgent.match(/iPhone|iPad|iPod/i); },
            Opera: function () { return navigator.userAgent.match(/Opera Mini/i); },
            Windows: function () { return navigator.userAgent.match(/IEMobile/i); },
            any: function () { return (isMobile.Android() || isMobile.BlackBerry() || isMobile.iOS() || isMobile.Opera() || isMobile.Windows()); }
        };

        if (body.hasClass('navbar-fixed') && !isMobile.any()) {
            body.removeClass('navbar-fixed');
        }
    }
}

