jQuery(document).ready(function () {
    jQuery('#main-menu > .menu-item-has-children > a.dropdown-toggle, #main-menu-home > .menu-item-has-children > a.dropdown-toggle').click(function (e) {
        e.preventDefault();
        window.location = jQuery(this).attr('href');
    });


    if (jQuery('#filter')) {
        jQuery('#filter .nav > li > a').click(function (e) {
            e.preventDefault();
            //pulisci prodotti e richiama il pager
            jQuery(".showcase-item-img").remove();
            $("#currentPage").val(0);
            jQuery('#hiddenFilter').attr('value', jQuery(this).attr('id'));
            //reload

            if (jQuery('#load_showcase').length == 0) {
                var html = "<a id='load_showcase' class='btn btn-primary' href='laser.orchard.webservices/webapi/display'>"
                    + jQuery('#textButtonShowShowcase').val() + "</a> <div id='load_posts_loader'></div>"

                jQuery('#load-showcase-div-button').append(html);

                jQuery('#load_showcase').bind('inview', function (e, isInView) {
                    if (isInView) load_more_showcase(e);
                });
                jQuery(document).on('click', '#load_showcase', load_more_showcase);
            }
        });

        jQuery('.filter-label').click(function (e) {
            e.preventDefault();
            jQuery('#filter').toggleClass('open');
        });

    }

    if (jQuery('body').hasClass('home')) {
        // Sticky nav
        navbar_sticky();
        jQuery(document).on('scroll scrollstart', navbar_sticky);
        jQuery(window).resize(navbar_sticky_data_top);

        // showcase slider
        jQuery('#showcase-slider').slick({
            autoplaySpeed: 5000,
            autoplay: false,
            arrows: true,
        });
    }

    if (document.getElementById('team')) {
        jQuery('.team-card-face').click(function (e) {
            e.preventDefault();
            var check_class = jQuery(this).parent('.team-card').hasClass('active');
            jQuery('.team-card').removeClass('active');
            if (!check_class) jQuery(this).parent('.team-card').addClass('active');
        });
    }

    if (jQuery('body').hasClass('page-template-page-krake')) {
        jQuery('.krake-card-face').click(function (e) {
            e.preventDefault();
            var check_class = jQuery(this).parent('.krake-card').hasClass('active');
            jQuery('.krake-card').removeClass('active');
            if (!check_class) jQuery(this).parent('.krake-card').addClass('active');
        });
    }
});

(function () {
    var width, height, largeHeader, canvas, ctx, points, target, animateHeader = true;

    if (jQuery('#intro #intro-canvas').length > 0) {
        initHeader();
        initAnimation();
        if (jQuery('body').hasClass('home')) addListeners();
    }

    function initHeader() {
        complextity = 13;

        width = window.innerWidth;
        height = window.innerHeight;

        if (!jQuery('body').hasClass('home')) {
            //height=height/2;
            //if(height<470) height=470;
            //if(height<jQuery('#intro > .container').outerHeight()) 
            height = jQuery('#intro > .container').outerHeight();
        }


        if (width < 767) {
            height = jQuery('#intro').outerHeight();
        }

        if (width >= 768 && width < 1200) {
            complextity = 8;
        } else if (width < 768) {
            complextity = 5;
        }

        target = { x: width / 2, y: height / 2 };

        largeHeader = document.getElementById('intro');
        largeHeader.style.height = height + 'px';

        canvas = document.getElementById('intro-canvas');
        canvas.width = width;
        canvas.height = height;
        ctx = canvas.getContext('2d');

        // create points
        points = [];
        for (var x = 0; x < width; x = x + width / complextity) {
            for (var y = 0; y < height; y = y + height / complextity) {
                var px = x + Math.random() * width / complextity;
                var py = y + Math.random() * height / complextity;
                var p = { x: px, originX: px, y: py, originY: py };
                points.push(p);
            }
        }

        // for each point find the 5 closest points
        for (var i = 0; i < points.length; i++) {
            var closest = [];
            var p1 = points[i];
            for (var j = 0; j < points.length; j++) {
                var p2 = points[j]
                if (!(p1 == p2)) {
                    var placed = false;
                    for (var k = 0; k < 5; k++) {
                        if (!placed) {
                            if (closest[k] == undefined) {
                                closest[k] = p2;
                                placed = true;
                            }
                        }
                    }

                    for (var k = 0; k < 5; k++) {
                        if (!placed) {
                            if (getDistance(p1, p2) < getDistance(p1, closest[k])) {
                                closest[k] = p2;
                                placed = true;
                            }
                        }
                    }
                }
            }
            p1.closest = closest;
        }

        // assign a circle to each point
        for (var i in points) {
            var c = new Circle(points[i], 2 + Math.random() * 2, 'rgba(255,255,255,0.3)');
            points[i].circle = c;
        }
    }

    // Event handling
    function addListeners() {
        if (!('ontouchstart' in window)) {
            window.addEventListener('mousemove', mouseMove);
        }
        window.addEventListener('scroll', scrollCheck);
        window.addEventListener('resize', resize);
    }

    function mouseMove(e) {
        var posx = posy = 0;
        if (e.pageX || e.pageY) {
            posx = e.pageX;
            posy = e.pageY;
        }
        else if (e.clientX || e.clientY) {
            posx = e.clientX + document.body.scrollLeft + document.documentElement.scrollLeft;
            posy = e.clientY + document.body.scrollTop + document.documentElement.scrollTop;
        }
        target.x = posx;
        target.y = posy;
    }

    function scrollCheck() {
        if (document.body.scrollTop > height) animateHeader = false;
        else animateHeader = true;
    }

    function resize() {
        width = window.innerWidth;
        height = window.innerHeight;
        largeHeader.style.height = height + 'px';
        canvas.width = width;
        canvas.height = height;
    }

    // animation
    function initAnimation() {
        animate();
        for (var i in points) {
            shiftPoint(points[i]);
        }
    }

    function animate() {
        if (animateHeader) {
            ctx.clearRect(0, 0, width, height);
            for (var i in points) {
                // detect points in range

                /*if(Math.abs(getDistance(target, points[i])) < 4000) {
                    points[i].active = (jQuery('body').hasClass('home')) ? 0.7 : 0.1;
                    points[i].circle.active = (jQuery('body').hasClass('home')) ? 1 : 0.1;
                } else if(Math.abs(getDistance(target, points[i])) < 20000) {
                    points[i].active = (jQuery('body').hasClass('home')) ? 0.3 : 0.1;
                    points[i].circle.active = (jQuery('body').hasClass('home')) ? 0.5 : 0.1;
                } else if(Math.abs(getDistance(target, points[i])) < 40000) {
                    points[i].active = (jQuery('body').hasClass('home')) ? 0.1 : 0.1;
                    points[i].circle.active = (jQuery('body').hasClass('home')) ? 0.3 : 0.1;
                } else {
                    points[i].active = 0;
                    points[i].circle.active = (jQuery('body').hasClass('home')) ? 0.05 : 0.1;
                }*/
                if (Math.abs(getDistance(target, points[i])) < 4000) {
                    points[i].active = (jQuery('body').hasClass('home')) ? 0.1 : 0;
                    points[i].circle.active = (jQuery('body').hasClass('home')) ? 0.1 : 0;
                } else if (Math.abs(getDistance(target, points[i])) < 20000) {
                    points[i].active = (jQuery('body').hasClass('home')) ? 0.1 : 0;
                    points[i].circle.active = (jQuery('body').hasClass('home')) ? 0.1 : 0;
                } else if (Math.abs(getDistance(target, points[i])) < 40000) {
                    points[i].active = (jQuery('body').hasClass('home')) ? 0.1 : 0;
                    points[i].circle.active = (jQuery('body').hasClass('home')) ? 0.1 : 0;
                } else {
                    points[i].active = 0;
                    points[i].circle.active = (jQuery('body').hasClass('home')) ? 0.05 : 0.1;
                }

                drawLines(points[i]);
                points[i].circle.draw();
            }
        }
        requestAnimationFrame(animate);
    }

    function shiftPoint(p) {
        TweenLite.to(p, 1 + 1 * Math.random(), {
            x: p.originX - 50 + Math.random() * 100,
            y: p.originY - 50 + Math.random() * 100, ease: Circ.easeInOut,
            onComplete: function () {
                shiftPoint(p);
            }
        });
    }

    // Canvas manipulation
    function drawLines(p) {
        if (!p.active) return;
        for (var i in p.closest) {
            ctx.beginPath();
            ctx.moveTo(p.x, p.y);
            ctx.lineTo(p.closest[i].x, p.closest[i].y);
            //ctx.strokeStyle = 'rgba(156,217,249,'+ p.active+')';
            ctx.strokeStyle = 'rgba(0,0,0,' + p.active + ')';
            ctx.stroke();
        }
    }

    function Circle(pos, rad, color) {
        var _this = this;

        // constructor
        (function () {
            _this.pos = pos || null;
            _this.radius = rad || null;
            _this.color = color || null;
        })();

        this.draw = function () {
            if (!_this.active) return;
            ctx.beginPath();
            ctx.arc(_this.pos.x, _this.pos.y, _this.radius, 0, 2 * Math.PI, false);

            if (jQuery('body').hasClass('home')) ctx.fillStyle = 'rgba(255,9,3,' + _this.active + ')';
            else ctx.fillStyle = 'rgba(70,70,70,' + _this.active + ')';
            ctx.fill();
        };
    }

    // Util
    function getDistance(p1, p2) {
        return Math.pow(p1.x - p2.x, 2) + Math.pow(p1.y - p2.y, 2);
    }

})();